import React, { useContext, useState } from 'react';

import { Transaction } from './Transaction';
import { GlobalContext } from '../context/GlobalState';

export const TransactionList = () => {
    const { transactions, deleteTransaction } =  useContext(GlobalContext);
    const [delID, setDelID] = useState('');

    function _delete(delID){
        deleteTransaction(delID);
    }

    return (
        <>

            { <select id='history' className="" onChange={()=>deleteTransaction(document.getElementById('history').value)}>
                <option> -- Transaction History -- </option>
                {transactions.map(transaction => (<Transaction key={transaction.id} transaction={transaction} />))}
            </select>}
            <button onClick={() => {_delete(delID)}} className="">Delete</button>
            {/* <ul className="list">
                {transactions.map(transaction => (<Transaction key={transaction.id} transaction={transaction} />))}
            </ul> */}
        </>
    )
}
