import React, { useContext } from 'react';
import { GlobalContext } from '../context/GlobalState';

export const IncomeExpense = () => {
    const { transactions } = useContext(GlobalContext);
    const total = amounts.reduce((acc, item) => (acc += item), 0).toFixed(2);

    const amounts = transactions.map(transaction => transaction.amount);
    const income = amounts
        .filter(item => item > 0)
        .reduce((acc, item) => (acc += item), 0)
        .toFixed(2);
    const expense = (
        amounts.filter(item => item < 0).reduce((acc, item) => (acc += item), 0) *
        -1
    ).toFixed(2);

    return (
        <div className="inc-exp-container"> 
            <div class="divTable">
                <div class="divTableBody">
                    <div class="divTableRow">
                        <div class="divTableCell money">Income</div>
                        <div class="divTableCell money plus">{ income }</div>
                    </div>
                    <div class="divTableRow">
                        <div class="divTableCell money">Expense</div>
                        <div class="divTableCell  money minus">{ expense }</div>
                    </div>
                    <div class="divTableRow">
                        <div class="divTableCell balance">Your Balance</div>
                        <div class="divTableCell balance">${total}</div>
                    </div>
                </div>
            </div>
            <h3 className="newTransac">Transaction History</h3>
        </div>








